create database if not exists flexpath_final;
use flexpath_final;

drop table if exists users, roles;

create table users (
	uuid char(36) primary key,
    username varchar(255),
    password varchar(255)
);

create table roles (
	uuid char(36),
    username varchar(255) not null,
    role varchar(250) not null,
    primary key (uuid, role),
    foreign key (uuid) references users(uuid) on delete cascade
);

create table books (
	id int auto_increment PRIMARY KEY,
    name VARCHAR(100)
);
create table sings (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100),
    owner_id VARCHAR(100),
    start_date DATE not null,
    end_date DATE, -- This can be null, if so we assume single day event.
    /*
     when_description will be used to generate dates if the only data available is in the form of 'Third sunday of april before the blood moon while gemini is in decline'
     as that's what's in the CSV I have.
     PROBABLY won't be necessary for now. Look for a way to scrape actual dates.
     But! This is how events are commonly described in this community, so leave the field and work it out later
    */
    when_description VARCHAR(100), 
    start_time TIME ,
    end_time TIME,
    primary_book int,
    secondary_book int,
    contact_email VARCHAR(320), -- 320 == limit of length for a valid email + @ symbol
    user_added_note VARCHAR(1000), -- Various notes i.e. Behind the barn next to the church or whatever
    location POINT NOT NULL,
    SPATIAL INDEX(location),
    foreign key(primary_book) references books(id),
    foreign key(secondary_book) references books(id),
    foreign key(owner_id) references users(uuid)
);

CREATE TABLE searches(
	id int auto_increment primary key,
    search_start DATE,
    search_end DATE,
    radius float4,
    search_location POINT not null,
    SPATIAL INDEX(search_location)
    );
    
